import React, { useEffect, useState } from "react";
import "./Coin.css";
import { Button } from "web3uikit"


function Coin({ perc, setBtc, token }) {

  const [color, setColor] = useState();

  useEffect(() => {
    if (perc < 50) {
      setColor("red")
    } else {
      setColor("green")
    }
  }, [perc])


  return (
    <>
      <div>
        <div class="token">
          {token}
        </div>
        <div class="circle" style={{ boxShadow: `0 0 20px ${color}` }}>
          <div
            class="wave"
            style={{
              marginTop: `${100 - perc}%`,
              boxShadow: `0 0 20px ${color}`,
              backgroundColor: color,
            }}
          >

          </div>
          <div class="percentage">
            {perc}%
          </div>
        </div>

        <div class="votes">
          <Button
            onClick={() => { setPerc(perc + 1) }}
            text="Up"
            theme="primary"
            type="button"
          />

          <Button
            onClick={() => { setPerc(perc - 1) }}
            color="red"
            text="Down"
            theme="colored"
            type="button"
          />
        </div>

      </div>
    </>
  );
}

export default Coin;
