import React, { useEffect, useState } from "react";
import "./Coin.css";


function Coin({ perc, setBtc, token }) {
  return (
    <>
      <div>
        <div class="token">
          {token}
        </div>
        <div class="circle">
          <div
            class="wave"
            style={{
              marginTop: `${100 - perc}%`,
              boxShadow: `0 0 20px green`,
              backgroundColor: "green",
            }}
          >

          </div>
          <div class="percentage">
            {perc}
          </div>
        </div>

      </div>
    </>
  );
}

export default Coin;
