import React, { useEffect, useState } from "react";
import "./Coin.css";


function Coin({ perc, setBtc, token }) {

  const [color, setColor] = useState();

  useEffect(() => {
    if (perc < 50) {
      setColor("c43d08")
    } else {
      setColor("green")
    }
  }, [perc])


  return (
    <>
      <div>
        <div class="token">
          {token}
        </div>
        <div class="circle" style={{ boxShadow: `0 0 20px ${color}` }}>
          <div
            class="wave"
            style={{
              marginTop: `${100 - perc}%`,
              boxShadow: `0 0 20px ${color}`,
              backgroundColor: color,
            }}
          >

          </div>
          <div class="percentage">
            {perc}
          </div>
        </div>

      </div>
    </>
  );
}

export default Coin;
